import React, { useState } from 'react';
import '../css/SigaPage.css';

const SigaPage = () => {
    const [data, setData] = useState([
        {
          nome: 'João da Silva',
          cpfCnpj: '123.456.789-00',
          autos: [
            {
              id: 1,
              auto: '123/2023',
              orgao: 'IBAMA',
              valor: 1000,
              dataAutuacao: '2023-08-25',
              localAutuacao: 'Reserva Ambiental',
              artigoInfringido: 'Artigo 123',
            },
            // ... (outros autos fictícios)
          ],
        },
        // ... (outros dados fictícios)
      ]);


      const addRow = () => {
        const newRow = {
          nome: '',
          cpfCnpj: '',
          autos: [],
        };
        setData([...data, newRow]);
      };

  const removeRow = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const updateRow = (index, field, value) => {
    const newData = [...data];
    newData[index][field] = value;
    setData(newData);
  };

  const addAuto = (index) => {
    const newData = [...data];
    newData[index].autos.push({ id: Date.now(), auto: '', orgao: '' });
    setData(newData);
  };

  const removeAuto = (index, autoId) => {
    const newData = [...data];
    newData[index].autos = newData[index].autos.filter((auto) => auto.id !== autoId);
    setData(newData);
  };

  return (
    <div className="siga-page">
      <h1>Sistema Integrado de Gestão Ambiental (SIGA)</h1>
      <table>
        <thead>
          <tr>
            <th>Nome do Autuado</th>
            <th>CPF/CNPJ do Autuado</th>
            <th>Autos de Infração</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  value={row.nome}
                  onChange={(e) => updateRow(index, 'nome', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={row.cpfCnpj}
                  onChange={(e) => updateRow(index, 'cpfCnpj', e.target.value)}
                />
              </td>
              <td>
                {row.autos.map((auto) => (
                  <div key={auto.id}>
                    <input
                      type="text"
                      value={auto.auto}
                      onChange={(e) => updateRow(index, 'auto', e.target.value)}
                      placeholder="Auto de Infração"
                    />
                    <input
                      type="text"
                      value={auto.orgao}
                      onChange={(e) => updateRow(index, 'orgao', e.target.value)}
                      placeholder="Órgão"
                    />
                    <button onClick={() => removeAuto(index, auto.id)}>Remover</button>
                  </div>
                ))}
                <button onClick={() => addAuto(index)}>Adicionar Auto</button>
              </td>
              <td>
                <button onClick={() => removeRow(index)}>Remover</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={addRow}>Adicionar Linha</button>
    </div>
  );
};

export default SigaPage;
